#!/bin/bash
# Example: Run NCBI BLAST verification with a simple test case
#
# This script demonstrates how to verify LOSAT's implementation
# against NCBI BLAST using a simple test case.
#
# Note: This requires NCBI BLAST+ to be installed and in PATH

set -e

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
cd "$SCRIPT_DIR"

# Check if blastn is available
if ! command -v blastn &> /dev/null; then
    echo "Error: blastn not found in PATH"
    echo ""
    echo "Please install NCBI BLAST+ or add it to your PATH"
    echo "You can download it from: https://ftp.ncbi.nlm.nih.gov/blast/executables/blast+/"
    echo ""
    echo "Alternatively, you can:"
    echo "  1. Run NCBI BLAST manually with the parameters shown in generate_ncbi_reference.sh"
    echo "  2. Use the output to manually create test cases"
    exit 1
fi

# Create a simple test sequence
TEST_DIR="test_sequences"
mkdir -p "$TEST_DIR"

# Create a simple query sequence (1000 bp)
cat > "$TEST_DIR/query.fasta" << 'EOF'
>test_query

EOF

# Copy the same sequence as subject (self-comparison)
cp "$TEST_DIR/query.fasta" "$TEST_DIR/subject.fasta"

echo "Created test sequences:"
echo "  Query: $TEST_DIR/query.fasta (1000 bp)"
echo "  Subject: $TEST_DIR/subject.fasta (1000 bp)"
echo ""

# Run verification
echo "Running verification..."
./verify_ncbi_implementation.sh \
    "$TEST_DIR/query.fasta" \
    "$TEST_DIR/subject.fasta" \
    "simple_test" \
    blastn

echo ""
echo "Verification complete!"
echo "Check the generated test case in: ncbi_reference_data/simple_test_test_case.rs"

